import React from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { Entypo, SimpleLineIcons } from '@expo/vector-icons';
import { NavigationContainer } from "@react-navigation/native";
import { createDrawerNavigator } from '@react-navigation/drawer';
import backups from '../screens/backups';
import Categories from '../screens/Categories';
import Contact from '../screens/Contact';
import Customize from '../screens/Customize';
import Home from '../screens/Home';
import Settings from '../screens/Settings';

const Drawer = createDrawerNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Drawer.Navigator
        screenOptions={{
          drawerStyle:{
            backgroundColor: "#fff",
            width: 250
          },
          headerStyle:{
            backgroundColor: "f4511e"
          },
          headerTintColor: "fff",
          headerTitleStyle:{
            fontWeight: "bold"
          },
          drawerActiveTintColor: "blue",
          drawerLabelStyle:{
            color: "#111"
          }
        }}
      >
        <Drawer.Screen 
          name="Home"
          options={{
            drawerLabel: "Home",
            title: "Home",
            drawerIcon: () => (
              <SimpleLineIcons name="home" size={28} color="#808080"/>
            )
          }}
          component={Home}
        /> 
      </Drawer.Navigator>
    </NavigationContainer>
  )
}

const Stack = createStackNavigator();

const HomeScreen = ({ navigation }) => {
  return (
    <Stack.Navigator>
      <Stack.Screen
        name="Home"
        component={HomeScreenContent}
        options={{
          headerTitle: () => (
            <View style={{ flexDirection: 'row', alignItems: 'center' }}>
              <Image
                source={require('../assets/logo.png')} // Replace with the path to your logo image
                style={{ width: 50, height: 50, marginRight: 10 }}
              />
              <Text style={{ fontSize: 20 }}>ENTE NADU</Text>
            </View>
          ),
          headerTitleAlign: 'center', // Center align the header title
          headerLeft: () => (
            <TouchableOpacity
              style={{ marginLeft: 10 }}
              onPress={() => alert('Menu Icon Pressed')}>
              <Entypo name="menu" size={24} color="black" />
            </TouchableOpacity>
          ),
        }}
      />
    </Stack.Navigator>
  );
};

const HomeScreenContent = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.message}>Home Screen</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  message: {
    fontSize: 24,
    fontWeight: 'bold',
  },
});
