import React, { useEffect } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, AccessibilityInfo } from 'react-native';
import { createStackNavigator } from '@react-navigation/stack';
import { Entypo } from '@expo/vector-icons';

const Stack = createStackNavigator();

const HomeScreen = ({ navigation }) => {
  useEffect(() => {
    // Set accessibility focus when the component mounts
    AccessibilityInfo.announceForAccessibility('Menu button is accessible.');
  }, []);

  const handleMenuPress = () => {
    alert('Menu Icon Pressed');
  };

  return (
    <Stack.Navigator>
      <Stack.Screen
        name="Home"
        component={HomeScreenContent}
        options={{
          headerTitle: () => (
            <View style={{ flexDirection: 'row', alignItems: 'center' }}>
              <Image
                source={require('../assets/logo.png')} // Replace with the path to your logo image
                style={{ width: 50, height: 50, marginRight: 10 }}
              />
              <Text style={{ fontSize: 20 }}>ENTE NADU</Text>
            </View>
          ),
          headerTitleAlign: 'center', // Center align the header title
          headerRight: () => (
            <TouchableOpacity
              style={{ marginRight: 10 }}
              onPress={() => alert('Header Button Pressed')}>
            </TouchableOpacity>
          ),
          headerLeft: () => (
            <TouchableOpacity
              style={{ marginLeft: 10 }}
              onPress={handleMenuPress}>
              <Entypo name="menu" size={24} color="black" />
            </TouchableOpacity>
          ),
        }}
      />
    </Stack.Navigator>
  );
};

const HomeScreenContent = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.message}>Home Screen</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  message: {
    fontSize: 24,
    fontWeight: 'bold',
  },
});

export default HomeScreen;
